css:
html, body {
    height: 100%;
    box-sizing: border-box; 
    margin: 0 0px;
}
header {
    padding: 0px 15px; 
    padding: 10px;
    text-align: center;
}

h2 {
    text-align: center; 
    font-size: 20px;
    color: #ff6600;
}
.underline {
    display: inline-block; 
    margin: 10;
    padding: 0;
    border-bottom: 2px solid #d3d3d3; 
    width: 300px; 
    text-align: center;
}
p {
    font-size: 12px;
}

footer {
    padding: 0;
    text-align: center;
    left: 0;  
    bottom: 0;  
    width: 100%;
}

.content {
    flex: 1;
}

.container {
    display: flex;
    justify-content: space-between;
    align-items: flex-start;
    margin: 0;
    padding: 0;
    box-sizing: border-box;
    width: 100%;
}

.logo-container {
    flex: 1; 
    text-align: left;
}

.logo-container p {
    margin: 0;
}

.logo-container img {
    width: 120px;
    height: 120px;
    object-fit: contain;
    display: block;
}

.header-container {
    flex: 1;
    text-align: right;
}

table {
    border-collapse: collapse;
    width: 100%;
    margin: 5px 0;
    max-width: 100%;
}
th, td {
    padding: 8px;
    text-align: left;
    font-size: 12px;
}
th {
    background-color: #2898cb;
    color: #FFFFFF !important;
    font-weight: bold; 
    line-height: 1;
}
table, th, td {
    border: none;
}
.border-right {
    border-right: 0.25px solid white;
}
.right-align {
    text-align: right;
}

.left-align {
    text-align: left;
}

.center-align {
    text-align: center;
}
.col-1, .col-2, .col-3 {
    margin: 0;

}
.col-1 {
    width: 40%;
    font-size: 12px;
    line-height: 0.5;
}

.col-2 {
    color: #2898cb;
    width: 35%;
    text-align: right;
    font-size: 12px;
    line-height: 0.5;
}

.col-3 {
    width: 25%;
    text-align: right;
    font-size: 12px;
    line-height: 0.5;
}

.footer-container {
    display: flex;
    width: 100%;
}

.footer div {
    margin: 0;
    padding: 0;
}

.footer {
    display: flex;
    width: 100%;
}

.footer .section-1 {
    flex: 40%;
}

.footer .section-2 {
    flex: 20%;
}

.footer .section-3 {
    flex: 40%;
}

.border-top-left {
    border-top: 1px solid #d3d3d3;
    padding-top: 10px;
    margin: 0;
    padding: 0 10px;
    text-align: center;
}

.border-top-right {
    border-top: 1px solid #d3d3d3;
    padding-top: 10px;
    margin: 0;
    text-align: center;
}

.custom-table {
    width: 100%;
    border-collapse: collapse;
    margin-top: -10px;
    margin-bottom: -10px;
    font-size: 12px;
}
.custom-col {
    display: table-cell;
    padding: 5px; /* ลดช่องว่างภายในเซลล์ */
    line-height: 1; /* ลดช่องว่างระหว่างบรรทัด */
    vertical-align: middle; /* จัดข้อความให้อยู่ตรงกลาง */
    text-align: center; /* จัดข้อความให้อยู่กึ่งกลางแนวนอน */
}
.custom-row {
    display: table-row;
}

.custom-col-1, .custom-col-2, .custom-col-3 {
    display: table-cell;
    padding: 0px;
    line-height: 0.2;
}

.custom-col-1 {
    margin: -20;
    width: 180px;
    text-align: left;
    color: #ff6600;
    text-indent: 40px;
}

.custom-col-2 {
    margin: 10;
    width: 180px;
    text-align: left;
    text-indent: 30px;
}

.custom-col-3 {
    width: 150px;
}

.inline-header {
    display: inline-block;
    margin: 0 10px;
    padding: 0;
    border-top: 2px solid #d3d3d3;
    width: 70%;
    text-align: center;
    font-size: 12px;
    line-height: 40px;
    vertical-align: top;
    font-weight: normal;
}
.with-border {
    border-bottom: 2px solid #d3d3d3;
}
.payment-details {
    position: absolute;  
    bottom: 250px; /* ระยะห่างจากด้านล่าง */
    width: 100%; 
    background-color: #f1f1f1; 
    padding: 10px 0;
    font-size: 16px; 
}
@media print {
    .print-format {
        padding-bottom: 50px;
        font-size: 16px;
    }
    .footer {
        position: fixed; 
        bottom: 0;
        width: 100%;
    }
}
@media screen {
    .print-format {
        padding: 0px 15px; 
        zoom: 1; 
        transform-origin: top left;
    }
  }
