css:
@import url('https://fonts.googleapis.com/css2?family=TH+Sarabun+New:wght@400;700&display=swap');

    .print-format {
        font-family: "TH Sarabun New" !important;
    }
@media print {
            @page {
                size: A4;
                margin: 5mm; 
            }
        }
        body {
            font-family: 'TH Sarabun New', sans-serif;
            margin: 0;
            padding: 0;
            box-sizing: border-box;
        }
        .document-section {
            margin-bottom: 10mm; 
        }

        table {
            width: 100%;
            border-collapse: collapse;
        }

        th, td {
            padding: 0px;
            text-align: left;
        }
        th{
            color: #000000 !important;
        }

        .footer {
            bottom: 0;
            text-align: center;
            font-size: 12px;
        }

        .compact {
            margin: 0;
            padding: 0;
        }
        .container {
        display: flex;
        justify-content: space-between; 
        align-items: flex-start; 
        padding: 10px; 
    }

.item {
    width: 30%; 
}

    .logo-container {
    text-align: left; 
    padding: 0;
    width: 100px;
    height: 100px;
    object-fit: contain;
}
    .border-right {
    border-right: 0.25px solid white;
}
.right-align {
    text-align: right;
}

.left-align {
    text-align: left;
}

.center-align {
    text-align: center;
}
.col-1, .col-2, .col-3 {
    margin: 0;

}
.col-1 {
    width: 50%;
    font-size: 12px;
    line-height: 0.5;
}

.col-2 {
    color:  #ff6600;
    width: 25%;
    text-align: right;
    font-size: 12px;
    line-height: 0.5;
}

.col-3 {
    width: 15%;
    text-align: right;
    font-size: 12px;
    line-height: 0.5;
}

.border-top-left {
    border-top: 1px solid #d3d3d3;
    padding-top: 10px;
    margin: 0;
    padding: 0 10px;
    text-align: center;
}

.border-top-right {
    border-top: 1px solid #d3d3d3;
    padding-top: 10px;
    margin: 0;
    text-align: center;
}

.custom-table {
    width: 40%;
    border-collapse: collapse;
    margin-top: -10px;
    margin-bottom: -10px;
    font-size: 12px;
}

.custom-row {
    display: table-row;
}

.custom-col-1, .custom-col-2 {
    display: table-cell;
    padding: 0px;
    line-height: 0.2;
}

.custom-col-1 {
    margin: -5;
    width: 30%;
    text-align: left;
    color: #ff6600;
    text-indent: 10px;
}

.custom-col-2 {
    margin: 10;
    width: 70%;
    text-align: left;
    text-indent: -40px;
}
.inline-header {
    display: inline-block;
    margin: 0 10px;
    border-top: 2px solid #d3d3d3;
    width: 40%;
    text-align: center;
    font-size: 12px;
    line-height: 30px;
    vertical-align: top;
    font-weight: normal;
}
.with-border {
    border-bottom: 2px solid #d3d3d3;
}
@media screen {
    .print-format {
        padding: 0px 15px; 
        zoom: 1; 
        transform-origin: top left;
    }
  }
