css:
html, body {
    height: 100%;
    box-sizing: border-box; 
    margin: 0 0px;
}
header {
    padding: 0px 15px; 
    padding: 10px;
    text-align: center;
}

h2 {
    text-align: center; 
    font-size: 20px;
    color: #e62067;
}
.underline {
    display: inline-block; 
    margin: 10;
    padding: 0;
    border-bottom: 2px solid #d3d3d3; 
    width: 300px; 
    text-align: center;
}
p {
    font-size: 12px;
}

footer {
    padding: 0;
    text-align: center;
    left: 0;  
    bottom: 0;  
    width: 100%;
}

.content {
    flex: 1;
}

.container {
    display: flex;
    justify-content: space-between;
    align-items: flex-start;
    margin: 0;
    padding: 0;
    box-sizing: border-box;
    width: 100%;
}

.logo-container {
    flex: 1; 
    text-align: left;
}

.logo-container p {
    margin: 0;
}

.logo-container img {
    width: 120px;
    height: 120px;
    object-fit: contain;
    display: block;
}

.header-container {
    flex: 1;
    text-align: right;
}

table {
    border-collapse: collapse;
    width: 100%;
    margin: 5px 0;
    max-width: 100%;
}
th, td {
    padding: 8px;
    text-align: left;
    font-size: 12px;
}
th {
    background-color: #e62067;
    color: #FFFFFF !important;
    font-weight: bold; /* ตัวหนังสือตัวหนา */
    line-height: 1;
}
table, th, td {
    border: none;
}
.border-right {
    border-right: 0.25px solid white; /* เส้นขอบด้านขวาสีขาว */
}
.right-align {
    text-align: right;
}

.left-align {
    text-align: left;
}

.center-align {
    text-align: center;
}
.col-1, .col-2, .col-3 {
    margin: 0;

}
.col-1 {
    width: 55%;
    font-size: 12px;
    line-height: 0.5;
}

.col-2 {
    color: #e62067;
    width: 20%;
    text-align: right;
    font-size: 12px;
    line-height: 0.5;
}

.col-3 {
    width: 15%;
    text-align: right;
    font-size: 12px;
    line-height: 0.5;
}

.footer-container {
    display: flex;
    width: 100%;
}

.footer div {
    margin: 0;
    padding: 0;
}

.footer {
    display: flex;
    width: 100%;
}

.footer .section-1 {
    flex: 40%;
}

.footer .section-2 {
    flex: 20%;
}

.footer .section-3 {
    flex: 40%;
}

.border-top-left {
    border-top: 1px solid #d3d3d3;
    padding-top: 10px;
    margin: 0;
    padding: 0 10px;
    text-align: center;
}

.border-top-right {
    border-top: 1px solid #d3d3d3;
    padding-top: 10px;
    margin: 0;
    text-align: center;
}

.custom-table {
    width: 100%;
    border-collapse: collapse;
    margin-bottom: -10px;
    font-size: 12px;
}

.custom-row {
    display: table-row;
}

.custom-col-1, .custom-col-2, .custom-col-3 {
    display: table-cell;
    line-height: 0.2;
}

.custom-col-1 {
    width: 120px;
    text-align: left;
    color: #e62067;

}

.custom-col-2 {
    width: 180px;
    text-align: left;

}
.custom-col-3 {
    width: 150px;
}
.custom-col-1-1 {
    width: 120px;
    text-align: left; 
    color: #e62067;
    line-height: 1.2; 
    box-sizing: border-box; 
    padding-top: 0; 
    padding-bottom: 5px;
}
.custom-col-2-1 {
    width: 180px;
    text-align: left;
    padding-left: 30px; /* ใช้ padding แทน text-indent */
    overflow-wrap: break-word; 
    word-wrap: break-word; 
    word-break: break-all;  
    line-height: 1.2; 
    box-sizing: border-box; 
    padding-top: 0;
    padding-bottom: 5px;
}

.inline-header {
    display: inline-block;
    margin: 0 10px;
    padding: 0;
    border-top: 2px solid #d3d3d3;
    width: 70%;
    text-align: center;
    font-size: 12px;
    line-height: 40px;
    vertical-align: top;
    font-weight: normal;
}
.with-border {
    border-bottom: 2px solid #d3d3d3;
}
@media print {
    .print-format {
        padding-bottom: 50px; /* เพิ่มระยะห่างด้านล่างให้มากพอเพื่อไม่ให้เนื้อหาทับ footer */
        font-size: 16px;
    }
    .footer {
        position: fixed; /* ทำให้ footer อยู่ที่ตำแหน่งคงที่ */
        bottom: 0;
        width: 100%;
    }
}
@media screen {
    .print-format {
        padding: 0px 15px; 
        zoom: 1; /* ย่อขนาดการแสดงผลในหน้าพรีวิว */
        transform-origin: top left;
    }
  }
